query bssListChildProductOfferings($productOfferingId: String!, $filters: [String!], $limit: Int, $offset: Int) {
  bssListChildProductOfferings(productOfferingId: $productOfferingId) @filter(filters: $filters) @limit(offset: $offset, limit: $limit) {
    id
    name
    isRoot
    availableTo
    prices {
      priceValues {
        priceTaxIncluded
        priceTaxExcluded
        __typename
            }
      tax
      chars {
        id
        name
        values
        __typename
            }
      type
      currency {
        currencyCode
        __typename
            }
      __typename
        }
    assignedTags {
      id
      name
      tagGroup {
        name
        __typename
            }
      __typename
        }
    product {
      name
      chars {
        name
        value
        originalValue
        originalName
        values {
          name
          __typename
                }
        __typename
            }
      params {
        name
        value {
          name
          __typename
                }
        values {
          value
          __typename
                }
        __typename
            }
      __typename
        }
    __typename
    }
}
